def getTulipID(graph):
    id_list = []
    id = graph.getStringProperty('id')
    reaction_true = graph.getBooleanProperty('reaction')
    for n in graph.getNodes():
        if reaction_true[n] == True:
            id_list.append(id[n])
    return id_list

def requestBiocyc(ID,liste):
    URL = "https://websvc.biocyc.org/getxml?ECOLI:" + ID
    response = requests.get(URL, timeout = 2)
    if response.status_code not in liste :
        liste.append(response.status_code)
    if response.status_code == 200:
        doc = ET.fromstring(response.text)
    elif response.status_code == 429:
        time.sleep(60)
        response = requests.get(URL, timeout = 2)
        doc = ET.fromstring(response.text)
    return doc, liste

def getPathwayID(pathways, reaction, liste):
    doc,liste = requestBiocyc(reaction,liste)
    for e in doc.findall(".//in-pathway/Pathway"):
        ID = e.attrib['frameid']
        if ID not in pathways:
            pathways.append(ID)
   
def getPathways(reactions):
    pathways = []
    to_delete = []
    liste=[]
    s=requests.Session()
    for r in reactions:
        try:
            getPathwayID(pathways, r,liste)      
        except :
            to_delete.append(r)
        
    return pathways, to_delete, liste

def getReactionsID(data, pathway):
    reactions = []
    doc = requestBiocyc(pathway)
    for e in doc.findall(".//reaction-list/Reaction"):
        reactions.append(e.attrib['frameid'])
    data[pathway] = reactions
    return 

def getReactions(pathways):
    data = {}
    for p in pathways:
        try :
            getReactionsID(data, p)
        except :
            pass
    return data

def subgraph(graph,pathway_name,dictionnaire):
    list_reactions = dictionnaire[pathway_name]
    id_list=graph.getStringProperty('id')
    name_list=graph.getStringProperty('name')
    pathwayNodes =[]
    for n in graph.getNodes():
        if id_list[n] in list_reactions:
            pathwayNodes.append(n)
            for n2 in graph.getInOutNodes(n):
                pathwayNodes.append(n2)
    pathwaySg = graph.inducedSubGraph(pathwayNodes)
    pathwaySg.setName(pathway_name)
    
def delete_reactions_not_biocyc(graph,list_non_Biocyc):
   id_list=graph.getStringProperty('id')
   list_neigbor_non_Biocyc=[]
   list_neigbor_Biocyc=[]
   list_to_delete=list_non_Biocyc
   # Gather all IDs of neighbors (i.e who share a substrate)
   for n in graph.getNodes():
      if id_list[n] in list_non_Biocyc :
         for n2 in graph.getInOutNodes(n):
            list_neigbor_non_Biocyc.append(id_list[n2])
      else :
          for n3 in graph.getInOutNodes(n):
            list_neigbor_Biocyc.append(id_list[n3])
   # Create list with every element to delete
   for i in list_neigbor_non_Biocyc :
       if i not in list_neigbor_Biocyc :
           list_to_delete.append(i)
   # Delete nodes
   for n2 in graph.getNodes():
       if id_list[n2] in list_to_delete :
           graph.delNode(n2)
           
def resetSubGraph(parent, name):
    sg = parent.getSubGraph(name)
    if sg != None:
        parent.delAllSubGraphs(sg)
    return graph.addCloneSubGraph(name) 
    
      
